function burger(){const e=document.querySelector(".js-burger-btn"),c=document.querySelector(".js-burger-close"),t=document.querySelector(".js-burger");e.onclick=()=>{t.classList.add("o-active")},c.onclick=()=>{t.classList.remove("o-active")}}function toc(){const e=document.querySelector(".js-toc-btn"),c=document.querySelector(".js-toc"),t=c.querySelectorAll("a"),o=document.querySelector("body");e.addEventListener("click",()=>{c.classList.toggle("o-active"),o.classList.toggle("o-lock")});for(let e=0;e<t.length;e++)t[e].addEventListener("click",()=>{c.classList.remove("o-active"),o.classList.remove("o-lock")})}burger(),toc();